export default {
  props: ['task'],
  data() {
    return {
      taskTitle: null
    };
  },
  computed: {
    taskTitleInvalid() {
      return !this.taskTitle || this.taskTitle === this.task.title;
    }
  },
  methods: {
    saveTask() {
      if (!this.taskTitleInvalid) {
        let payload = {
          id: this.task.id,
          title: this.taskTitle
        };
        this.$store.dispatch('updateTaskTitle', payload);
        this.$emit('close');
      }
    }
  },
  mounted() {
    this.taskTitle = this.task.title;
  }
};
